import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import ChartComponent from './ChartComponent';

function App() {
  const [jsonData, setJsonData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const targetTimestamp = "2023-12-31T19:12:00.000+00:00"; // Change this to your target timestamp

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/sample-data.json'); // Adjust the path as needed
        const data = await response.json();
        setJsonData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures effect runs only once

  useEffect(() => {
    const filtered = jsonData.filter(item => item.time === targetTimestamp);
    setFilteredData(filtered);
  }, [jsonData, targetTimestamp]);

  return (
    <div>
      <h1>JSON Data:</h1>
      <div>
        {jsonData.map(item => (
          < ChartComponent ts={item.ts} status={item.machine_status} value={item.value} />
        ))}
      </div>
    </div>
  );
}

export default App;
